mapper.select:
SELECT *
FROM commontable
WHERE DELETEYN = 'N'

	AND cod = #{cod}


	AND client_cod LIKE CONCAT('%', #{clientCod}, '%')


	AND product_cod LIKE CONCAT('%', #{productCod}, '%')


	AND plan_date BETWEEN #{preSearchDate} AND
	#{postSearchDate}

ORDER BY cod DESC
LIMIT #{} OFFSET #{offset}

SELECT COUNT(*) FROM commontable WHERE DELETEYN = 'N'
AND post_id = #{searchNumber}
AND title LIKE CONCAT('%', #{searchTitle}, '%')
AND content LIKE CONCAT('%', #{searchContent}, '%')
AND author LIKE CONCAT('%', #{searchAuthor}, '%')
AND created_at BETWEEN #{preSearchDate} AND #{postSearchDate}
