mapper.select:
SELECT *
FROM estimate
WHERE DELETEYN = 'N'

	AND cod = #{cod}


	AND client_cod LIKE CONCAT('%', #{clientCod}, '%')


	AND employee_cod LIKE CONCAT('%', #{employeeCod}, '%')


	AND est_date BETWEEN #{preSearchDate} AND
	#{postSearchDate}

ORDER BY cod DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM estimate WHERE DELETEYN = 'N'
AND cod = #{cod}
AND client_cod LIKE CONCAT('%', #{clientCod}, '%')
AND employee_cod LIKE CONCAT('%', #{employeeCod}, '%')
AND est_date BETWEEN #{preSearchDate} AND #{postSearchDate}

mapper.update:
update estimatedetail
set qty = #{qty}
where estimate_cod = #{cod} and num = #{num}
