mapper.select:
SELECT i.*, w.name AS 'warehouse', e.name AS 'employee', u.name AS 'unit' FROM inventory i LEFT JOIN warehouse w ON i.warehouse_cod = w.cod LEFT JOIN employee e ON i.employee_cod = e.cod LEFT JOIN unit u ON i.unit_Cod = u.cod WHERE i.deleteyn = 'N'
AND i.cod = #{cod}
AND w.name LIKE CONCAT('%', #{warehouse}, '%')
AND e.name LIKE CONCAT('%', #{employee}, '%')
AND account LIKE CONCAT('%', #{account}, '%')
AND inv_Date BETWEEN #{preSearchDate} AND #{postSearchDate}
ORDER BY i.cod DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT count(*), w.name AS 'warehouse', e.name AS 'employee', u.name AS 'unit' FROM inventory i LEFT JOIN warehouse w ON i.warehouse_cod = w.cod LEFT JOIN employee e ON i.employee_cod = e.cod LEFT JOIN unit u ON i.unit_Cod = u.name WHERE i.deleteyn = 'N'
AND i.cod = #{cod}
AND w.name LIKE CONCAT('%', #{warehouse}, '%')
AND e.name LIKE CONCAT('%', #{employee}, '%')
AND account LIKE CONCAT('%', #{account}, '%')
AND inv_Date BETWEEN #{preSearchDate} AND #{postSearchDate}
