mapper.select:
SELECT i.cod, e.name AS employee, w.name AS warehouse, l.name AS location, ic.count_date, ic.inv_date, ic.countclass, ic.account, ic.note FROM inventorycount ic LEFT JOIN inventorycountdetail icd ON ic.cod = icd.inventorycount_cod LEFT JOIN inventory i ON icd.inventory_cod = i.cod LEFT JOIN product p ON icd.product_cod = p.cod LEFT JOIN employee e ON ic.employee_cod = e.cod LEFT JOIN warehouse w ON ic.warehouse_cod = w.cod LEFT JOIN location l ON ic.location_cod = l.cod WHERE ic.deleteyn = 'N'
AND ic.cod = #{searchCod}
AND employee LIKE CONCAT('%', #{searchEmployee}, '%')
AND warehouse LIKE CONCAT('%', #{searchWarehouse}, '%')
AND location LIKE CONCAT('%', #{searchLocation}, '%')
AND i.name = #{searchInventory}
AND p.name = #{searchProduct}
AND ic.count_date BETWEEN #{preSearchDate} AND #{postSearchDate}
ORDER BY ic.cod DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT count(*) FROM inventorycount ic LEFT JOIN inventorycountdetail icd ON ic.cod = icd.inventorycount_cod LEFT JOIN inventory i ON icd.inventory_cod = i.cod LEFT JOIN product p ON icd.product_cod = p.cod LEFT JOIN employee e ON ic.employee_cod = e.cod LEFT JOIN warehouse w ON ic.warehouse_cod = w.cod LEFT JOIN location l ON ic.location_cod = l.cod WHERE ic.deleteyn = 'N'
AND ic.cod = #{searchCod}
AND employee LIKE CONCAT('%', #{searchEmployee}, '%')
AND warehouse LIKE CONCAT('%', #{searchWarehouse}, '%')
AND location LIKE CONCAT('%', #{searchLocation}, '%')
AND i.name = #{searchInventory}
AND p.name = #{searchProduct}
AND ic.count_date BETWEEN #{preSearchDate} AND #{postSearchDate}

SELECT p.prodname as name, p.cod as cod, p.cur_inv_qty as computingQty, ia.procclass FROM product p LEFT JOIN inventorycountdetail icd ON icd.inventory_cod = p.cod LEFT JOIN inventoryadjustmentdetail iad ON icd.adjustmentdetail_num = iad.num LEFT JOIN inventoryadjustment ia ON ia.cod = iad.inventoryadjustment_cod WHERE p.cod=#{cod}
UNION
SELECT i.name as name, i.cod as cod, i.cur_inv_qty as computingQty, ia.procclass FROM inventory i LEFT JOIN inventorycountdetail icd ON icd.inventory_cod = i.cod LEFT JOIN inventoryadjustmentdetail iad ON icd.adjustmentdetail_num = iad.num LEFT JOIN inventoryadjustment ia ON ia.cod = iad.inventoryadjustment_cod WHERE i.cod=#{cod}
