mapper.select:
SELECT m.cod, m.plan_date, m.product_cod, p.prodname, p.spec, u.name as 'unitName', od.delivery_date, o.dday, m.qty, c.name as 'clientName', m.note FROM mps m JOIN product p ON m.product_cod = p.cod JOIN unit u ON p.unit_cod = u.cod JOIN orders o ON m.order_cod = o.cod JOIN orderdetail od ON o.cod = od.cod and m.orderdetail_num = od.num JOIN client c ON o.client_cod = c.cod WHERE m.deleteyn = 'N' AND m.closing = 'N'
AND m.plan = #{searchPlan}
AND m.product_cod = #{searchProdCod}
AND p.prodname LIKE CONCAT('%', #{searchProdName}, '%')
AND c.name LIKE CONCAT('%', #{searchClient}, '%')
AND m.plan_date BETWEEN #{preSearchDate} AND #{postSearchDate}
ORDER BY m.plan_date DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM mps m JOIN product p ON m.product_cod = p.cod JOIN unit u ON p.unit_cod = u.cod JOIN orders o ON m.order_cod = o.cod JOIN orderdetail od ON o.cod = od.cod and m.orderdetail_num = od.num JOIN client c ON o.client_cod = c.cod WHERE m.deleteyn = 'N' AND m.closing = 'N'
AND m.plan = #{searchPlan}
AND m.product_cod = #{searchProdCod}
AND p.prodname LIKE CONCAT('%', #{searchProdName}, '%')
AND c.name LIKE CONCAT('%', #{searchClient}, '%')
AND m.plan_date BETWEEN #{preSearchDate} AND #{postSearchDate}

mapper.update:
UPDATE mps
SET
plan_date = #{planDate}
qty = #{qty}
note = #{note}
WHERE cod = #{cod};
