mapper.select:
SELECT *
FROM orders
WHERE deleteyn = 'N'

	AND cod = #{cod}


	AND client_cod LIKE CONCAT('%', #{clientCod}, '%')


	AND employee_cod LIKE CONCAT('%', #{employeeCod}, '%')


	AND order_date BETWEEN #{preSearchDate} AND
	#{postSearchDate}

ORDER BY cod DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM orders WHERE DELETEYN = 'N'
AND cod = #{cod}
AND client_cod LIKE CONCAT('%', #{clientCod}, '%')
AND employee_cod LIKE CONCAT('%', #{employeeCod}, '%')
AND order_date BETWEEN #{preSearchDate} AND #{postSearchDate}
