mapper.select:
SELECT *
FROM salesplan
WHERE DELETEYN = 'N'

	AND cod = #{cod}


	AND client_cod LIKE CONCAT('%', #{clientCod}, '%')


	AND product_cod LIKE CONCAT('%', #{productCod}, '%')


	AND plan_date BETWEEN #{preSearchDate} AND
	#{postSearchDate}

ORDER BY cod DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM salesplan WHERE DELETEYN = 'N'
AND cod = #{cod}
AND client_cod LIKE CONCAT('%', #{clientCod}, '%')
AND product_cod LIKE CONCAT('%', #{productCod}, '%')
AND plan_date BETWEEN #{preSearchDate} AND #{postSearchDate}
